import React from 'react';

interface TabProps {
  tabs: React.ReactNode[];
  activeTab: number;
  onTabChange: (index: number) => void;
  tabStyle?: React.CSSProperties;
  activeTabStyle?: React.CSSProperties;
}

const Tab: React.FC<TabProps> = ({ tabs, activeTab, onTabChange, tabStyle, activeTabStyle }) => {

  return (
    <div>
      <div>
        {tabs.map((tab, index) => (
          <div
            key={index}
            onClick={() => onTabChange(index)}
            style={{
              display: 'inline-block',
              padding: '8px 16px',
              cursor: 'pointer',
              borderBottom: index === activeTab ? '2px solid #007bff' : '2px solid transparent',
              ...(index === activeTab ? activeTabStyle : tabStyle),
            }}
          >
            {tab}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Tab;
